	DROP VIEW [dbo].[VCNTStock]
GO

CREATE VIEW [dbo].[VCNTStock] AS SELECT
	TCNTStk.FTPdtCode, 
	TCNTStk.FTOrdCode, 
	TCNTStk.FNInvQty, 
	CAST(TCNTStk.FDInvDate AS DATE) AS FDInvDate
FROM
	TCNTStk
	INNER JOIN
	TCNMProduct
	ON 
		TCNTStk.FTPdtCode = TCNMProduct.FTPdtCode
